    header .utill { display: flex; align-items: center; font-size: 0.8rem;}
    header .utill > li { margin-left:20px; }
    header .bar_menu { display: none;}
    /* slide */
    .slide { width: 90%; margin: auto;}
    .slide .slide_bt { position: relative; right: 27vw; bottom: 2vw;  }
    .slide .slide_bt > div{ width: 7%;}
    .slide .button{ display: flex; justify-content: flex-end; position: relative; top: -6vw;}
    .slide .button a {width: 7%; height: 6vw; line-height: 6vw; background-color: #fff; color: #707070; text-align: center; font-size: 2rem; }
    .slide .button a:first-child {border-right:1px solid #000;}
    /* intro */
    .intro { width: 90%; background-color: #F9F9F9;}
    .intro > .int_box { padding: 140px 60px; height: 700px;}
    .intro > .int_box .int_txt_box { display: flex;}
    .intro > h1 {}
    .intro > p {}
    .intro > .int_box > .int_txt_box > .txt_box1 { width: 40%; margin-right: 50px; font-size: 1.2rem;}
    .intro > .int_box > .int_txt_box > .txt_box2 { font-size: 1.2rem;}
    .intro > .int_box > .int_txt_box > .txt_box2 > .txt > p > br { display: none; }
    .intro > .int_box > .int_txt_box { position:relative; }
    .intro > .int_box > .int_txt_box > .bt { position:absolute; bottom: -100px; left: 0; height: 45px; line-height: 45px; padding: 0; font-size: 1.2rem; width: 300px;}   
    .intro > .int_box > .int_txt_box > .bt > p > a{
       display: block; width: 100%; height: 45px; margin-left: 15px;
    } 
    .intro > .int_box > .int_txt_box > .bt > p > a > span:last-child {margin-left: 15px;}
    /* history */
    .liner { position: relative; bottom: 50px; }
    .history { height: 700px; margin-bottom: 50px;}
    .history > .his_box > .box{ float: left; margin: 0 30px;}
    .history > .v_line { position: relative; top: 500px; }
    .history > .v_line > p > a { font-size: 1rem; }
    .history > .v_line > p > a > span{ font-size: 0.7rem; margin-left: 10px; }
    /* best film */
    .best_film { width: 90%; background-color: #F9F9F9; margin-top: 50px; height: 950px; margin: auto;}
    .best_film > .liner2 { position: relative; bottom: 50px; }
    .best_film > .best_box  { width: 80%; margin: auto; overflow: hidden; display: flex; justify-content: center }
    .best_film > .best_box > .box2 { margin: 0 30px; }
    .best_film > .best_box > .box2 > a { display: block; }
    .best_film > .best_box > .box2 > a > img{ width: 310px; }
    .best_film > .v_line2 { position: relative; top: 150px; }
    .best_film > .v_line2 > p > a { font-size: 1rem; }
    .best_film > .v_line2 > p > a > span{ font-size: 0.7rem; margin-left: 10px; }
    /* 공지사항 */
    .Notice { margin-top: 50px; width: 80%;}
    .Notice > .Not > p > a{ font-size: 0; }
    .Notice > .nt_box > .nt_txtbox { display: flex; flex-direction: column; align-items: flex-start;}  
    .Notice > .nt_box  { font-size: 1.1rem;}  
    .Notice > .nt_box2  { font-size: 1rem; border-bottom: none;}  
    /* 뉴스 */
    .news {width: 80%;}
    .news p {width: 40%; line-height: 1.6; float: left;}
    .news > .new_bt {width: 40%; float: right; display: flex;}
    .news > .new_bt > input:nth-child(1) {width: 30%;}
    .news > .new_bt > input:nth-child(2) {width: 50%;margin-left: 10px;}
    .news > .new_bt > .button {width: 20%; margin-left: 10px;}
    /* 후원사 */
    .spon > .sp1 > a > img { display: none; }
    .spon > .sp2 > a > img { display: block; }
    /* 푸터 */
    footer { display: flex; width: 90%; margin: auto;}
    footer > .ft_lo { width:149px; margin:0 3% 0 10%}
    footer > .ft_tx > * {margin:10px 0;  display: flex; }
    footer > .ft_tx { padding:30px 0; }
    footer > .ft_tx > ul > li { margin: 0 10px 0 0; }
    footer > .ft_tx > ul > li:nth-child(2) { display: block }
    footer > .ft_tx > ul > li:nth-child(4) { display: block }
    footer > .ft_tx > .footer_title > .information {margin: 0 15px;}
    footer > .ft_lo, .portfolio { display:block; }
    footer > .portfolio { position: absolute; right: 150px;}
    footer > .portfolio > p > a { display: block; width: 200px; height: 50px; line-height: 50px; background-color: #014099; color: #fff; border-radius: 5px;}

}
